code.cread:
QVariant v;
in >> v;
assert(v.value<int>() == 5);
in >> v;
assert(v.value<QString>() == "nesamone");

code.cswrite:
QVariant<int> vi = new QVariant<int>(5);
QTypeManager.Serialize(fs, vi);
QVariant<string> vs = new QVariant<string>("nesamone");
QTypeManager.Serialize(fs, vs);

code.csread:
QVariant<int> v;
QTypeManager.Deserialize(fs, out v);
Debug.Assert(v.Value == 5);

QVariant<string> str;
QTypeManager.Deserialize(fs, out str);
Debug.Assert(str.Value == "nesamone");
